import { 
    StyleSheet,
    Text,
    View,
    Keyboard,
    KeyboardAvoidingView,
    Platform, 
    Alert,
    Image,
    TextInput,
} from 'react-native';

import { useState } from 'react';

import AuthPressable from '../components/auth/AuthPressable';

import { createClient } from '@supabase/supabase-js'

const THEME = '#3F3F3F';


const supabaseUrl = "https://aqeopdkkfhradtlezpil.supabase.co"
const supabaseAnonKey = 
"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImFxZW9wZGtrZmhyYWR0bGV6cGlsIiwicm9sZSI6ImFub24iLCJpYXQiOjE2NTM0NTIxMTEsImV4cCI6MTk2OTAyODExMX0.MZZovcPnuGFnM2wDyabFZAuL8ei9vZqlfxql4I849wA"

const supabase = createClient(supabaseUrl, supabaseAnonKey);


const SignInScreen = ({ navigation }) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // check if possible log in
    async function signInWithEmail() {
        const { user, error } = await supabase.auth.signIn({
            email: email,
            password: password,
        })

        if (error) {
            Alert.alert('Invalid Email / Password')
        } else {
            navigation.navigate("Home")
        }
    }

    const restoreForm = () => {
        setEmail(''); 
        setPassword('')
        Keyboard.dismiss(); 
    }

    const navigateToSignUp = () => {
        navigation.navigate("SignUp")
    }


    return (
        <KeyboardAvoidingView
            behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        >

            <View style={styles.container}>

                <Image
                    source = {require('../assets/test.png')}
                    style = {{width: 200, height: 220, bottom: 15}}
                />

                <Text style={[styles.title, styles.boldText]}>
                    NUSinteract
                </Text>

                <TextInput
                    style={styles.textInput}
                    placeholder='Email' 
                    KeyboardType='email-address'
                    value={email}
                    onChangeText={setEmail}
                    selectionColor={THEME}
                />

                <TextInput
                    style={styles.textInput}
                    placeholder='password' 
                    value={password}
                    onChangeText={setPassword}
                    selectionColor={THEME}
                    secureTextEntry
                />

                <AuthPressable
                    onPressHandler={signInWithEmail}    
                    title={'SIGN IN'}
                />
                <AuthPressable
                    onPressHandler={navigateToSignUp}    
                    title={'SIGN UP WITH NUS EMAIL'}
                />
            </View>
        </KeyboardAvoidingView>
    )
}

export default SignInScreen; 

const styles = StyleSheet.create({

    container: {
        backgroundColor: '#7FFFD4',
        height: '100%',
        justifyContent: 'center',
        alignItems: 'center',
    },

    textInput: {
        alignSelf: 'center',
        borderWidth: 2,
        borderColor: THEME, 
        borderRadius: 4, 
        width: '80%',
        height: 40, 
        paddingHorizontal: 8, 
        marginBottom: 10
    },


    title: {
        fontSize: 35, 
        fontFamily: "AvenirNext-Italic",
        textAlign: 'center',
        marginBottom: 20
    },

    boldText: {
        fontWeight: '500'
    }
});
